import axios from "axios";

import {setAlert} from './alert';

import { 
    POST_ERROR, 
    GET_POSTS 
} from "./types";

//Get posts

export const getPosts = () => async dispatch => {
    try {
        const res = await axios.get('/api/posts');

        dispatch({
            type: GET_POSTS,
            payload: res.data
        })
    } 
    catch (error) {
        dispatch({
            type: POST_ERROR,
            payload: {
                msg: error.response.data.msg,
                status: error.response.status
            }
        })
    }
};

//Add like

export const addLike = (id) => async dispatch => {
    try {
        const res = await axios.get('/api/posts');

        dispatch({
            type: GET_POSTS,
            payload: res.data
        })
    } 
    catch (error) {
        dispatch({
            type: POST_ERROR,
            payload: {
                msg: error.response.data.msg,
                status: error.response.status
            }
        })
    }
};
